import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.js";

const url = 'http://localhost:8000/getAllGameSelection'
function App() {
  const [user, setUser] = useState(null)
  const responseData = async () => {
    const returnCall = await fetch(url)
      .then(response => response.json())
      .then((responseData) => {
        return responseData;
      });
      console.log(returnCall)
      setUser(returnCall)
      return returnCall;
  }


useEffect(() => {
  responseData()
}, [])

const [texto, setTexto] = useState('')

const handleInputChange = ({target}) => {
  setTexto(target.value)
}

  return (
  <div className="container text-center">
    <div className="row">

      <div className="col">
        <h2>
          Bienvenido al sistema de recomendaci√≥n
        </h2>
      </div>

      <div className="row">

      <div>
            { !user ? 'Cargando...' :
            user.map((list) => {
              const {gamesList} = list
              return gamesList.map((value) => {
                return (
                  <div className="row">
                  <div className="col-md-6">
                    <h4>
                      {value}
                    </h4>
                  </div>
                <div className="col-md-6">
                  <input className="form-control" value={texto} onChange={handleInputChange}/>
                </div>
                </div>
                  )
              })
            })}
        </div>
      </div>

      <div>
      <button className="col btn btn-primary">
        Enviar calificaciones
      </button>
      </div>

    </div>
  </div>
  );
}

export default App;
