import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.js";

const url = 'http://localhost:8000/users'
function App() {
  const [user, setUser] = useState(null)
  const responseData = async () => {
    const returnCall = await fetch(url)
      .then(response => response.json())
      .then((responseData) => {
        return responseData;
      });
      console.log(returnCall)
      setUser(returnCall)
      return returnCall;
  }


useEffect(() => {
  responseData()
}, [])

  return (
  <div className="container text-center">
    <div className="row">

      <div className="col">
        <h2>
          Bienvenido al sistema de recomendaci√≥n
        </h2>
      </div>

      <div className="row">

      <div className="col-md-6">
            { !user ? 'Cargando...' :
            user.map((list) => {
              const {ProfileRecomendation} = list
              return ProfileRecomendation.map((value) => {
                return (
                  <div className="row">
                  <div className="col-md-6">
                    <h5>
                      {value}
                    </h5>
                  </div>
                <div className="col-md-6">
                  <input type="range" className="form-range" min="0" max="10" step="1" id="customRange3"/>
                </div>
                </div>
                  )
              })
            })}
        </div>
      </div>

      <div>
      <button className="col btn btn-primary">
        Enviar calificaciones
      </button>
      </div>

    </div>
  </div>
  );
}

export default App;
